import React, { useState } from 'react';

const Inquiry = () => {
  const [selectedButtons, setSelectedButtons] = useState([]);

  const toggleActive = (value) => {
    setSelectedButtons((prevSelected) =>
      prevSelected.includes(value)
        ? prevSelected.filter((item) => item !== value)
        : [...prevSelected, value]
    );
  };

  const isActive = (value) => selectedButtons.includes(value);

  return (
    <>
      <html data-theme="light">
        <header>
          <style>
            {`
              .large-text {
                font-size: 2em;
              }
              .btn-active {
                background-color: #17a2b8; /* Change to your desired active color */
                color: white;
              }
            `}
          </style>
          <div>
            <b>
              <strong>
                <span className="text-accent-content large-text">Drop Us a </span>
              </strong>
            </b>
          </div>
        </header>
        <div className="space-y-2">
          <div className="space-x-2">
            <button
              className={`btn btn-outline btn-info ${isActive('mobileDevelopment') ? 'btn-active' : ''}`}
              type="button"
              value="mobileDevelopment"
              onClick={() => toggleActive('mobileDevelopment')}
            >
              Mobile Development
            </button>
            <button
              className={`btn btn-outline btn-info ${isActive('webDevelopment') ? 'btn-active' : ''}`}
              type="button"
              value="webDevelopment"
              onClick={() => toggleActive('webDevelopment')}
            >
              Web Development
            </button>
            <button
              className={`btn btn-outline btn-info ${isActive('sqaSolution') ? 'btn-active' : ''}`}
              type="button"
              value="sqaSolution"
              onClick={() => toggleActive('sqaSolution')}
            >
              SQA Solution
            </button>
            <button
              className={`btn btn-outline btn-info ${isActive('UIux') ? 'btn-active' : ''}`}
              type="button"
              value="UIux"
              onClick={() => toggleActive('UIux')}
            >
              Web UX/UI Design
            </button>
            <button
              className={`btn btn-outline btn-info ${isActive('api') ? 'btn-active' : ''}`}
              type="button"
              value="api"
              onClick={() => toggleActive('api')}
            >
              API Integration
            </button>
          </div>
          <div className="space-x-2">
            <button
              className={`btn btn-outline btn-info ${isActive('mobileUiUx') ? 'btn-active' : ''}`}
              type="button"
              value="mobileUiUx"
              onClick={() => toggleActive('mobileUiUx')}
            >
              Mobile UX/UI Design
            </button>
            <button
              className={`btn btn-outline btn-info ${isActive('softwareDevelopment') ? 'btn-active' : ''}`}
              type="button"
              value="softwareDevelopment"
              onClick={() => toggleActive('softwareDevelopment')}
            >
              Software Development
            </button>
            <button
              className={`btn btn-outline btn-info ${isActive('customServices') ? 'btn-active' : ''}`}
              type="button"
              value="customServices"
              onClick={() => toggleActive('customServices')}
            >
              Custom Services
            </button>
          </div>
        </div>
      </html>
    </>
  );
};

export default Inquiry;
